//Reference used https://www.pluralsight.com/guides/creating-dynamic-editable-tables-with-reactjs
//https://www.pluralsight.com/guides/extracting-input-from-a-form-with-reactjs
import React from 'react';


export default class CustomerForm extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            customer: {
                date: props.date,
                name: props.lastName,
                status: props.status
            }
        }
    }


    handleDateChanged(event) {
        var customer = this.state.customer;
        customer.date = event.target.value;
        this.setState({ customer: customer });
    }

    handleLastNameChanged(event) {
        var customer = this.state.customer;
        customer.lastName = event.target.value;
        this.setState({ customer: customer });
    }

    handleStatusChanged(event) {
        var customer = this.state.customer;
        customer.status = event.target.value;
        this.setState({ customer: customer });
    }

    handleButtonClicked() {
        console.log(this.state.customer);
    }

    render() {
        return (
            <div>
                <label>
                    Date
                </label>
                <input type="date" value={this.state.customer.date} onChange={this.handleDateChanged.bind(this)} />
                <br />
                <label>
                    Last Name:
                </label>
                <input type="text" value={this.state.customer.lastName} onChange={this.handleLastNameChanged.bind(this)} />
                <br />
                <label>
                    Status:
                </label>
                <select value={this.state.customer.status} onChange={this.handleStatusChanged.bind(this)}>
                    <option value="PENDING">
                        Pending
                    </option>
                    <option value="APPROVED">
                        Approved
                    </option>
                </select>
                <hr />
                <button onClick={this.handleButtonClicked.bind(this)}>
                    Save Record
                </button>
            </div>
        );
    }

}
