//Reference used https://www.pluralsight.com/guides/creating-dynamic-editable-tables-with-reactjs
import React from 'react';
import ReactDOM from 'react-dom';
export default class App extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            date: "",
            name: Text,
            dateArr: [],
            nameArr: []
        }
    }

    updateDate(event) {
        this.setState({
            date: event.target.value,
        });
    }

    updateName(event) {
        this.setState({
            name: event.target.value,
        });
    }

    handleClick() {
        let dateArr = this.state.dateArr;
        let nameArr = this.state.nameArr;
        dateArr.push(this.state.date);
        nameArr.push(this.state.name);
        this.setState({
            dateArr: dateArr,
            nameArr: nameArr
        });

    }

    handleItemChanged(i, event) {
        let dateArr = this.state.dateArr;
        let nameArr = this.state.nameArr;
        dateArr[i] = event.target.value;
        nameArr[i] = event.target.value;
        this.setState({
            dateArr: dateArr,
            nameArr: nameArr
        });

    }

    handleItemDeleted(i) {
        let dateArr = this.state.dateArr;
        dateArr.splice(i, 1);
        let nameArr = this.state.nameArr;
        nameArr.splice(i, 1);
        this.setState({
            dateArr: dateArr,
            date: "",
            nameArr: nameArr,
            name:""

        });

    }

    renderRows() {
        let context = this;

        return this.state.dateArr.map(function (o, i) {
            return (
                <div>
                    <tr key={"item-" + i}>
                    <td>
                        <input type="date" value={o} onChange={context.handleItemChanged.bind(context, i)} />
                    </td>
                    </tr>
                </div>

            );
        })

        let Names = this.state.nameArr.map(function (o, i) {
            return (
                <div>
                    <tr key={"item-" + i}>
                    <td>
                        <input type="text" value={o} onChange={context.handleItemChanged.bind(context, i)} />
                    </td>
                    <td>
                        <button onClick={context.handleItemDeleted.bind(context, i)}>Remove</button>
                    </td>
                    </tr>
                </div>

            );
        })
    }

    render() {
        return (
            <div>
                <h1>NCSA Lab Hours</h1>
                <table className="">
                    <thead>
                        <tr>
                            <th>Date</th>
                            <th>Name</th>
                            <th>Add/Remove Item</th>
                        </tr>
                    </thead>
                    <tbody>{this.renderRows()}</tbody>
                </table>
                <hr />
                <input type="date" value={this.state.date} onChange={this.updateDate.bind(this)} />
                <input type="text" value={this.state.name} onChange={this.updateName.bind(this)} />
                <button onClick={this.handleClick.bind(this)}> Add Item </button>
            </div>
        );
    }
}
