//Reference used https://www.pluralsight.com/guides/creating-dynamic-editable-tables-with-reactjs
import React from 'react';

export default class App extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            name: "",
            nameArr: []
        }
    }
    updateMessage(event) {
        this.setState({
            name: event.target.value
        });
    }

    handleClick() {
        let nameArr = this.state.nameArr;
        nameArr.push(this.state.name);

        this.setState({
            nameArr: nameArr
        })

    }

    renderRows() {
        var context = this;
        return this.state.items.map(function (o, i) {
            return (
                <tr key={"item-" + i}>
                    <td>
                        <input type="date" value={o} onChange={context.handleItemChanged.bind(context, i)} />
                    </td>
                    <td>
                        <input type="text" value={o} onChange={context.handleItemChanged.bind(context, i)} />
                    </td>
                    <td>
                        <button onClick={context.handleItemDeleted.bind(context, i)}>Remove</button>
                    </td>
                </tr>
            );
        });
    }
    render() {
        return (
            <div>
                <table>
                    <thead>
                        <tr>
                            <th>Name</th>
                            <th>Add/Remove Name</th>
                        </tr>
                    </thead>
                    <tbody>
                    </tbody>
                </table>
                <hr/>
                <input type="text" onChange={this.updateMessage.bind(this)}/>
                <button onClick={this.handleClick.bind(this)}> Add Name</button>
            </div>
        );

    }
}
